#GENRE
CREATE TABLE Genres (
    id_genre int(4) NOT NULL AUTO_INCREMENT,
    lib_genre varchar(32) NOT NULL,
    CONSTRAINT pk_genres PRIMARY KEY (id_genre)
    );


#REALISATEUR
CREATE TABLE Realisateurs (
  id_realisateur int(4) NOT NULL AUTO_INCREMENT,
  nom_realisateur varchar(32) NOT NULL,
  prenom_realisateur varchar(32) NOT NULL,
  natio_realisateur varchar(32) NOT NULL,
  date_naiss_realisateur date NOT NULL,
  nb_films int(3),
  lien_img_realisateur varchar(1024) NOT NULL,
  CONSTRAINT pk_realisateurs PRIMARY KEY(id_realisateur)
);

#FILMS
CREATE TABLE Films (
  nom_film varchar(64) NOT NULL,
  date_sortie_film date NOT NULL,
  synopsis_film varchar(1024) NOT NULL,
  id_genre int(4) DEFAULT NULL,
  lien_img_film varchar(2014) NOT NULL,
  duree_min_film int(3) DEFAULT NULL,
  id_realisateur int(4) DEFAULT NULL,
  CONSTRAINT fk_realisteurs_films FOREIGN KEY (id_realisateur) REFERENCES Realisateurs(id_realisateur) ON DELETE SET NULL,
  CONSTRAINT fk_genres_films FOREIGN KEY (id_genre) REFERENCES Genres(id_genre) ON DELETE SET NULL
);

#Admin
CREATE TABLE Admin (
  id_admin int(3) NOT NULL AUTO_INCREMENT,
  pseudo_admin varchar(32) NOT NULL,
  mdp_admin varchar(64) NOT NULL,
  token_admin varchar(40) NOT NULL,
  CONSTRAINT pk_admin PRIMARY KEY(id_admin)
);


#TRIGGER qui augmente le nombre de film lors de l'insertion d'un film
CREATE TRIGGER `incremente_nb_film_realisateur` AFTER INSERT ON `films`
 FOR EACH ROW UPDATE Realisateurs
SET Realisateurs.nb_films=Realisateurs.nb_films+1
WHERE NEW.id_realisateur = Realisateurs.id_realisateur


#TRIGGER qui diminue le nombre de film lors de la suppression d'un film
CREATE TRIGGER `decremente_nb_film_realisateur` AFTER DELETE ON `films`
 FOR EACH ROW UPDATE Realisateurs
SET Realisateurs.nb_films=Realisateurs.nb_films-1
WHERE OLD.id_realisateur = Realisateurs.id_realisateur








